EventTypeTemplates.LevelTransition:
//Used for changing the map/level. given a new position
public static void [NAMEHERE](ref Map CurrentMap, String FileName, int newx, int newy, int movetime)
{
    //unload the map
    CurrentMap.UnloadMap();

    //get the level file!
    Level NewLevel = Level.ImportLevel(FileName);

    //Create New Map
    CurrentMap = new Map(){ level = NewLevel };
}

